// Process Apollo.io analysis and prepare database updates
const analysis = JSON.parse($node["Apollo Business Logic Analysis"].json.response);
const updates = [];
const alerts = [];
const metrics = [];

// Process sync updates
for (const update of analysis.hyperformant_sync_updates) {
  if (update.table === 'apollo_smart_lists') {
    updates.push({
      table: 'apollo_smart_lists',
      id: update.record_id,
      data: {
        actual_count: update.updates.actual_count,
        last_sync_at: new Date().toISOString(),
        ...update.updates.performance_data
      }
    });
  } else if (update.table === 'apollo_sequences') {
    updates.push({
      table: 'apollo_sequences',
      id: update.record_id,
      data: {
        last_sync_at: new Date().toISOString(),
        ...update.updates.performance_data
      }
    });
  }
}

// Process high priority actions
for (const action of analysis.high_priority_actions) {
  if (action.urgency === 'high') {
    alerts.push({
      type: 'apollo_performance_alert',
      title: `${action.action_type} required for ${action.target_id}`,
      description: action.reason,
      priority: action.urgency,
      action_required: action.action_type,
      target_id: action.target_id
    });
  }
}

// Prepare daily metrics update
const today = new Date().toISOString().split('T')[0];
let totalContacts = 0;
let totalSends = 0;
let totalOpens = 0;
let totalReplies = 0;
let totalMeetings = 0;

for (const perf of analysis.apollo_performance_analysis) {
  totalContacts += perf.key_metrics.total_contacts || 0;
  totalSends += perf.key_metrics.sequence_sends || 0;
  totalOpens += Math.floor((perf.key_metrics.open_rate || 0) * (perf.key_metrics.sequence_sends || 0));
  totalReplies += Math.floor((perf.key_metrics.reply_rate || 0) * (perf.key_metrics.sequence_sends || 0));
  totalMeetings += perf.key_metrics.meeting_rate || 0;
}

metrics.push({
  metric_date: today,
  apollo_total_contacts: totalContacts,
  apollo_sequence_sends: totalSends,
  apollo_sequence_opens: totalOpens,
  apollo_sequence_replies: totalReplies,
  apollo_meetings_booked: totalMeetings
});

return {
  updates: updates,
  alerts: alerts,
  daily_metrics: metrics,
  analysis_summary: {
    lists_analyzed: analysis.apollo_performance_analysis.length,
    optimizations_identified: analysis.optimization_recommendations.length,
    high_priority_actions: analysis.high_priority_actions.filter(a => a.urgency === 'high').length,
    business_intelligence: analysis.business_intelligence
  },
  oracle_notification_data: {
    top_performing_segments: analysis.business_intelligence.top_performing_segments,
    underperforming_segments: analysis.business_intelligence.underperforming_segments,
    conversion_rates: analysis.business_intelligence.apollo_to_hyperformant_conversion_rates,
    optimization_recommendations: analysis.optimization_recommendations.filter(r => r.priority === 'high')
  }
};

