// Process Apollo.io webhook event
const webhookData = $node["Apollo.io Webhook Receiver"].json;
const eventType = webhookData.type || webhookData.event_type;
const eventData = webhookData.data || webhookData;

// Prepare webhook event record for database
const webhookEvent = {
  webhook_id: webhookData.webhook_id || 'apollo_webhook',
  event_type: eventType,
  apollo_object_type: webhookData.object_type || 'unknown',
  apollo_object_id: webhookData.object_id || eventData.id,
  event_data: eventData,
  processing_status: 'pending',
  received_at: new Date().toISOString()
};

// Determine processing priority
const highPriorityEvents = [
  'sequence_replied',
  'meeting_booked',
  'contact_created',
  'sequence_finished'
];

const processingPriority = highPriorityEvents.includes(eventType) ? 'high' : 'medium';

// Determine if Oracle Agent should be notified immediately
const oracleNotificationEvents = [
  'sequence_replied',
  'meeting_booked',
  'high_value_contact_created'
];

const shouldNotifyOracle = oracleNotificationEvents.includes(eventType);

return {
  webhook_event: webhookEvent,
  processing_priority: processingPriority,
  should_notify_oracle: shouldNotifyOracle,
  event_type: eventType,
  apollo_object_id: webhookEvent.apollo_object_id
};
